const input = $input.first().json.body;

// Validate required fields
if (!input.tenant_id || !input.scope || !input.task) {
  return [{
    json: {
      error: 'Missing required fields: tenant_id, scope, task'
    }
  }];
}

// Validate task is an object
if (typeof input.task !== 'object' || input.task === null) {
  return [{
    json: {
      error: 'task must be an object'
    }
  }];
}

const requestId = input.request_id || `req-${Date.now()}-${Math.random().toString(36).slice(2,8)}`;

// Simulate executor output
const output = {
  timestamp: new Date().toISOString(),
  task: input.task,
  status: 'received',
  executor: 'ai-executor'
};

return [{
  json: {
    tenant_id: input.tenant_id,
    scope: input.scope,
    task: input.task,
    output: output,
    outcome: 'ok',
    request_id: requestId
  }
}];

const data = $input.first().json;

if (data.error) {
  return [{
    json: {
      status: 'error',
      message: data.error
    }
  }];
}

return [{
  json: data
}];

const validated = $('Check Validation').first().json;

if (validated.status === 'error') {
  return [{ json: validated }];
}

const response = $input.first().json;
const policy = (response && typeof response.result === 'object')
  ? response.result
  : {
      policy_id: 'fallback',
      policy_version: 'fallback',
      decision: 'deny',
      allow: false,
      requires_approval: true,
      risk_score: 0,
      reasons: ['invalid_policy_response']
    };

if (policy.decision !== 'allow') {
  return [{
    json: {
      status: 'error',
      message: policy.decision === 'requires_approval' ? 'Policy requires approval' : 'Policy denied',
      request_id: validated.request_id,
      policy,
      approval: policy.decision === 'requires_approval' ? {
        endpoint: '/webhook/policy/approval',
        method: 'POST'
      } : null
    }
  }];
}

return [{
  json: {
    ...validated,
    policy
  }
}];

const data = $input.first().json;

if (data.status === 'error') {
  return [{ json: data }];
}

const task = data.task || {};
const payload = task.payload || {};

if (task.type !== 'security_digest_mail') {
  return [{ json: data }];
}

const message = [
  '以下の事実データから、読みやすい日本語メルマガ本文を作成してください。',
  '',
  '要件:',
  '- 構成: 1) 今日の要点(3行) 2) 重要トピックTOP3 3) 業務影響の見方 4) 今日やることチェックリスト(5項目以内)',
  '- 誇張せず、根拠は事実データのみ',
  '- 1通で2-3分で読める分量',
  '- 平易で実務に使える日本語',
  '',
  '事実データ:',
  String(payload.message || ''),
].join('\n');

return [{
  json: {
    ...data,
    _brain_enabled: true,
    brain_input: {
      tenant_id: data.tenant_id || 'corp-internal',
      scope: data.scope || 'security-digest',
      message,
      meta: {
        source: '04_executor_dispatch',
        use_case: 'daily_digest_executor'
      },
      brain: {
        provider: task.llm?.provider || 'openrouter',
        model: task.llm?.model || 'qwen/qwen3-next-80b-a3b-instruct:free',
        temperature: 0.3
      }
    }
  }
}];

const brainResp = $input.first().json || {};
const context = $('Prepare Brain Payload').first().json._executor_context || {};

if (!context || Object.keys(context).length === 0) {
  return [{ json: brainResp }];
}

const task = context.task || {};
const payload = task.payload || {};

if (task.type !== 'security_digest_mail') {
  return [{ json: context }];
}

const llmText = (brainResp.ok === true && String(brainResp.status || '').toUpperCase() === 'OK')
  ? String(brainResp.answer || '').trim()
  : '';

const finalMessage = llmText
  ? `${llmText}\n\n---\n[Source Facts]\n${String(payload.message || '')}`
  : String(payload.message || '');

const safeSubject = String(payload.subject || 'Daily Security Digest').replace(/</g, '&lt;').replace(/>/g, '&gt;');
const safeBody = finalMessage.replace(/</g, '&lt;').replace(/>/g, '&gt;');
const finalHtml = `<h2>${safeSubject}</h2><pre>${safeBody}</pre>`;

const updatedTask = {
  ...task,
  payload: {
    ...payload,
    message: finalMessage,
    htmlMessage: finalHtml
  }
};

return [{
  json: {
    ...context,
    task: updatedTask,
    output: {
      ...(context.output || {}),
      task: updatedTask,
      llm: {
        provider: brainResp.provider || 'openrouter',
        status: brainResp.status || 'ERROR'
      }
    },
    outcome: llmText ? 'ok' : 'ok_with_local_fallback'
  }
}];
