parameters.jsCode:
const validated = $('Check Validation').first().json;

if (validated.status === 'error') {
  return [{ json: validated }];
}

const response = $input.first().json;
const policy = (response && typeof response.result === 'object')
  ? response.result
  : {
      policy_id: 'fallback',
      policy_version: 'fallback',
      decision: 'deny',
      allow: false,
      requires_approval: true,
      risk_score: 0,
      reasons: ['invalid_policy_response']
    };

if (policy.decision !== 'allow') {
  return [{
    json: {
      status: 'error',
      message: policy.decision === 'requires_approval' ? 'Policy requires approval' : 'Policy denied',
      request_id: validated.request_id,
      policy,
      approval: policy.decision === 'requires_approval' ? {
        endpoint: '/webhook/policy/approval',
        method: 'POST'
      } : null
    }
  }];
}

return [{
  json: {
    ...validated,
    policy
  }
}];
