const input = $input.first().json.body || {};

if (!input.request_id || !input.decision || !input.approver) {
  return [{ json: { status: 'error', message: 'Missing required fields: request_id, decision, approver' } }];
}

const validDecisions = ['approved', 'rejected'];
if (!validDecisions.includes(input.decision)) {
  return [{ json: { status: 'error', message: 'decision must be approved or rejected' } }];
}

return [{
  json: {
    request_id: String(input.request_id),
    decision: input.decision,
    approver: String(input.approver),
    comment: String(input.comment || ''),
    approved_at: new Date().toISOString(),
    policy_id: String(input.policy_id || 'unknown'),
    policy_version: String(input.policy_version || 'unknown')
  }
}];

const data = $input.first().json;
if (data.status === 'error') {
  return [{ json: data }];
}

const auditDecision = data.decision === 'approved' ? 'allowed' : 'denied';
return [{ json: { ...data, auditDecision } }];
