const body = $input.first().json.body || {};

const workflow_id = String(body.workflow_id || '').trim();
const task_type = String(body.task_type || '').trim();
const tenant_id = String(body.tenant_id || '*').trim();
const scope_pattern = String(body.scope_pattern || '*').trim();
const enabled = body.enabled !== false;
const actor = String(body.actor || 'n8n-ce');
const constraints = body.constraints && typeof body.constraints === 'object' ? body.constraints : {};

if (!workflow_id || !task_type) {
  return [{ json: { status: 'error', message: 'workflow_id and task_type are required' } }];
}

return [{
  json: { workflow_id, task_type, tenant_id, scope_pattern, enabled, actor, constraints }
}];
