// Normalize input - never throw, always return valid object
const input = $json || {};

// Extract required fields with defaults
const tenant_id = (input.tenant_id || 'unknown').toString().trim();
const scope = (input.scope || 'unknown').toString().trim();
const message = (input.message || input.text || '').toString();
const meta = input.meta || {};
const brain = input.brain || {};

// Validate (collect errors, don't throw)
const errors = [];
if (!tenant_id || tenant_id === 'unknown') errors.push('tenant_id required');
if (!scope || scope === 'unknown') errors.push('scope required');
if (!message) errors.push('message required');

// Determine provider (none, openai, kimi)
let provider = (brain.provider || 'none').toString().toLowerCase();
if (!['openai', 'kimi', 'none'].includes(provider)) {
  provider = 'none';
}

// Return normalized input
return [{
  json: {
    tenant_id,
    scope,
    message,
    meta,
    brain: {
      provider,
      model: brain.model || 'gpt-4',
      temperature: typeof brain.temperature === 'number' ? brain.temperature : 0.2
    },
    validation_errors: errors.length > 0 ? errors : null
  }
}];

const data = $input.first().json;

// Return ERROR response for validation failures
const errorMsg = data.validation_errors.join(', ');
return [{
  json: {
    ok: true,
    status: 'ERROR',
    provider: 'none',
    slack_text: `⚠️ *Brain Router Error*\n\nValidation failed: ${errorMsg}`,
    answer: `Error: ${errorMsg}`,
    tenant_id: data.tenant_id,
    scope: data.scope,
    timestamp: new Date().toISOString(),
    debug: { http: 200, error: data.validation_errors }
  }
}];

const data = $input.first().json;

// Return NO_BRAIN response when provider is 'none'
return [{
  json: {
    ok: true,
    status: 'NO_BRAIN',
    provider: 'none',
    slack_text: '⚠️ *AI Brain Not Configured*\n\nNo LLM provider configured. Set brain.provider to "openai" or "kimi" to enable AI features.\n\nInput received successfully but no processing performed.',
    answer: 'AI brain is not configured. Please configure KIMI_API_KEY or OPENAI_API_KEY.',
    tenant_id: data.tenant_id,
    scope: data.scope,
    timestamp: new Date().toISOString(),
    debug: { http: 200, error: null }
  }
}];

const data = $input.first().json;
const httpData = $input.all()[0]?.json || {};

// Extract HTTP response details
const statusCode = httpData.statusCode || httpData.status || 0;
const body = httpData.body || {};

// Check if successful
if (statusCode >= 200 && statusCode < 300 && body.choices && body.choices[0]) {
  const content = body.choices[0].message?.content || 'No response content';
  return [{
    json: {
      ok: true,
      status: 'OK',
      provider: 'openai',
      slack_text: content,
      answer: content,
      tenant_id: data.tenant_id,
      scope: data.scope,
      timestamp: new Date().toISOString(),
      debug: { http: statusCode, error: null }
    }
  }];
}

// Error or no response
const errorMsg = body.error?.message || `HTTP ${statusCode} error`;
return [{
  json: {
    ok: true,
    status: 'ERROR',
    provider: 'openai',
    slack_text: `⚠️ *OpenAI Error*\n\n${errorMsg}`,
    answer: errorMsg,
    tenant_id: data.tenant_id,
    scope: data.scope,
    timestamp: new Date().toISOString(),
    debug: { http: statusCode, error: body.error || errorMsg }
  }
}];

