// Hardcoded safe defaults - no env var access (sandbox restriction)
const maxChars = 6000;
const maxK = 10;

// Support both flat and nested body structure
const body = $json.body || $json;
const tenant_id = (body.tenant_id || "").trim();
const scope = (body.scope || "").trim();
let message = (body.message || body.text || "").toString();

if (!tenant_id) throw new Error("tenant_id required");
if (!scope) throw new Error("scope required");
if (!message) throw new Error("message required");

if (message.length > maxChars) {
  message = message.slice(0, maxChars);
}

let k = parseInt(body.k || "5", 10);
if (!Number.isFinite(k) || k < 1) k = 5;
if (k > maxK) k = maxK;

// Check for explicit brain_enabled flag
const brain_enabled = body.brain_enabled === true || body.brain_enabled === "true";

return [{
  json: {
    tenant_id,
    scope,
    message,
    k,
    brain_enabled
  }
}];

const data = $input.first().json;

// Always return NO_BRAIN - no env var access allowed in sandbox
// Infrastructure is working, but no LLM provider configured
return [{
  json: {
    status: "NO_BRAIN",
    provider: "none",
    answer: "⚠️ AI brain is not configured yet.\n\nPlease configure KIMI_API_KEY or OPENAI_API_KEY in .env and restart services.\n\n(Infra is working; only the LLM provider is missing.)",
    tenant_id: data.tenant_id,
    scope: data.scope,
    context_count: 0,
    answer_length: 185,
    timestamp: new Date().toISOString()
  }
}];

