const raw = $json;

// HTTP Request V3 raw response often comes as string in $json or $json.body
let text = '';
if (typeof raw === 'string') {
  text = raw;
} else if (typeof raw?.body === 'string') {
  text = raw.body;
} else if (typeof raw?.data === 'string') {
  text = raw.data;
}

// If still empty, check if raw is already an object (sometimes parsed)
if (!text && typeof raw === 'object' && raw !== null) {
  // Already parsed JSON object
  if (raw.payload || raw.id) {
    return [{ json: raw }];
  }
  // Check body
  if (raw.body && (raw.body.payload || raw.body.id)) {
    return [{ json: raw.body }];
  }
}

// If text is empty, return diagnostic
if (!text || !text.trim()) {
  return [{
    json: {
      status: "METADATA_EMPTY",
      rawType: typeof raw,
      rawKeys: Object.keys(raw || {}),
      note: "Gmail API returned empty response"
    }
  }];
}

// Parse the JSON text
try {
  const parsed = JSON.parse(text);
  return [{ json: parsed }];
} catch (e) {
  return [{
    json: {
      status: "METADATA_PARSE_ERROR",
      error: String(e),
      sample: text.slice(0, 200),
      rawType: typeof raw
    }
  }];
}

const d = $json;

// Support normalized shape (body.payload) and direct shape (payload)
const msg = d.body?.payload ? d.body : (d.payload ? d : null);

if (!msg) {
  return [{ json: { status: 'METADATA_INVALID', note: 'No payload found' } }];
}

// Get headers from correct location
const headers = msg.payload?.headers || [];
const get = (name) => {
  const h = headers.find(h => (h.name || '').toLowerCase() === name.toLowerCase());
  return h ? h.value : 'Unknown';
};

const from = get('From');
const subject = get('Subject');
const date = get('Date');
const internalDate = Number(msg.internalDate || 0);
const id = msg.id || 'unknown';

// Read cursor from Load Cursor node
// Postgres returns an array of rows, so we need to access the first row
let lastSeen = 0;
let cursorDebug = 'not loaded';
try {
  const loadCursor = $('Load Cursor');
  if (loadCursor && loadCursor.first()) {
    const cursorRow = loadCursor.first().json;
    console.log('Cursor row:', JSON.stringify(cursorRow));

    // Handle both array format (Postgres) and object format
    if (Array.isArray(cursorRow) && cursorRow.length > 0) {
      lastSeen = Number(cursorRow[0].last_seen_internal_date || 0);
      cursorDebug = 'array format, lastSeen=' + lastSeen;
    } else if (cursorRow && cursorRow.last_seen_internal_date !== undefined) {
      lastSeen = Number(cursorRow.last_seen_internal_date || 0);
      cursorDebug = 'object format, lastSeen=' + lastSeen;
    } else {
      cursorDebug = 'unexpected format: ' + JSON.stringify(cursorRow).slice(0, 100);
    }
  } else {
    cursorDebug = 'Load Cursor node has no output';
  }
} catch(e) {
  cursorDebug = 'error: ' + String(e);
  lastSeen = 0;
}

console.log('Dedupe check:', { id, internalDate, lastSeen, cursorDebug });

// Dedupe check - if internalDate is 0 or <= lastSeen, skip
if (!internalDate || internalDate <= lastSeen) {
  console.log('Skipping old email:', id);
  return [{ json: { status: 'SKIP_OLD', id, internalDate, lastSeen, cursorDebug } }];
}

console.log('New email found:', id);
return [{
  json: {
    status: 'NEW',
    id,
    threadId: msg.threadId || id,
    from,
    subject,
    date,
    internalDate,
    link: `https://mail.google.com/mail/u/0/#inbox/${id}`
  }
}];

// Old or duplicate email - no action needed
return [{
  json: {
    status: 'SKIP_OLD',
    id: $json.id,
    note: 'Email already processed or older than cursor'
  }
}];

// No messages found
return [{
  json: {
    status: 'NO_MESSAGES',
    note: 'No messages in INBOX or all filtered',
    timestamp: new Date().toISOString()
  }
}];

const input = $input.first().json;

let status = 0;
let body = {};
let error = null;

// Handle fullResponse shape (HTTP Request with fullResponse: true)
if (input.statusCode !== undefined) {
  status = input.statusCode;
  body = input.body || {};
} else if (input.status !== undefined && typeof input.status === 'number') {
  status = input.status;
  body = input.body || input;
} else if (input.messages !== undefined || input.resultSizeEstimate !== undefined) {
  // Direct Gmail API response shape
  body = input;
  status = 200;
} else {
  // Unknown shape - could be error
  body = input;
  status = input.statusCode || input.status || 0;
}

// Check for error indicators
if (input.error || input.message) {
  error = input.error || input.message;
}

return [{
  json: {
    ok: (status >= 200 && status < 300),
    status,
    body,
    error,
    _source: 'Gmail List (INBOX)'
  }
}];

const input = $input.first().json;

let status = 0;
let body = {};
let error = null;
let raw = null;

// HTTP Request with fullResponse: true returns statusCode and body
if (input.statusCode !== undefined) {
  status = input.statusCode;
  raw = input.body;
} else if (input.status !== undefined && typeof input.status === 'number') {
  status = input.status;
  raw = input.body || input;
} else {
  // Direct response
  raw = input;
  status = 200;
}

// Parse raw response if it's a string (JSON text)
if (typeof raw === 'string') {
  try {
    body = JSON.parse(raw);
  } catch (e) {
    error = String(e);
    body = { rawText: raw.slice(0, 1000) };
  }
} else if (typeof raw === 'object' && raw !== null) {
  body = raw;
} else {
  body = {};
}

// Check for error indicators
if (input.error || input.message) {
  error = input.error || input.message;
}

// Check for Gmail API error in body
if (body.error) {
  error = body.error.message || JSON.stringify(body.error);
  status = body.error.code || status;
}

return [{
  json: {
    ok: (status >= 200 && status < 300 && !error),
    status,
    body,
    error,
    _source: 'Gmail Get Metadata (RAW)'
  }
}];

// Get email data from Dedupe + Format node output
const email = $input.first().json;

// Extract email content for the prompt
const from = email.from || 'Unknown';
const subject = email.subject || 'No Subject';
const date = email.date || 'Unknown';
const id = email.id || 'unknown';
const threadId = email.threadId || id;

// Build prompt text for Router
// Include instructions for output format
const promptText = `[Gmail Email Analysis]

From: ${from}
Subject: ${subject}
Date: ${date}
Message ID: ${id}

Please analyze this email and provide:

1. SUMMARY: A concise summary in 6 lines or less

2. PRIORITY: Rate as High/Medium/Low with brief reason

3. NEXT ACTIONS: Bullet points of suggested actions

4. DRAFT REPLY: A polite business draft reply (in Japanese by default; if the email appears to be in English, provide English draft)

Please format your response clearly with these sections.`;

// Build Router-compatible input shape
const routerInput = {
  brain_enabled: true,
  type: "event_callback",
  team_id: "gmail",
  event_id: `gmail-${id}`,
  event_time: Math.floor(Date.now() / 1000),
  event: {
    type: "message",
    text: promptText,
    user: "gmail",
    channel: "gmail",
    ts: "0",
    thread_ts: "0"
  },
  tenant_id: "gmail",
  scope: "email-analysis",
  meta: {
    source: "gmail",
    message_id: id,
    thread_id: threadId,
    from: from,
    subject: subject,
    date: date
  }
};

return [{ json: routerInput }];

const input = $input.first().json;

let status = 0;
let body = null;
let text = '';
let error = null;
let ok = false;

// Handle HTTP fullResponse shape
if (input.statusCode !== undefined) {
  status = input.statusCode;
  body = input.body;
} else if (input.status !== undefined && typeof input.status === 'number') {
  status = input.status;
  body = input.body || input;
} else {
  // Direct response (non-HTTP or already parsed)
  body = input;
  status = 200;
}

// Check for HTTP error
if (status < 200 || status >= 300) {
  error = `HTTP ${status}`;
  ok = false;
} else {
  ok = true;
}

// Extract text from body
if (typeof body === 'string') {
  text = body;
  // Try to parse as JSON
  try {
    body = JSON.parse(body);
  } catch (e) {
    // Keep as string
    body = null;
  }
} else if (typeof body === 'object' && body !== null) {
  // If body has 'answer' field (Router response format), use that as text
  if (body.answer) {
    text = body.answer;
  } else if (body.text) {
    text = body.text;
  } else {
    // Convert object to string representation
    text = JSON.stringify(body, null, 2);
  }
}

// Check for error in body
if (body && body.error) {
  error = body.error.message || body.error;
  ok = false;
}

return [{
  json: {
    ok,
    status,
    router_body: body,
    router_text: text,
    error
  }
}];

