const crypto = require('crypto');
const secret = $env.SLACK_SIGNING_SECRET;
if (!secret) throw new Error('Missing SLACK_SIGNING_SECRET');
const ts = $headers['x-slack-request-timestamp'];
const sig = $headers['x-slack-signature'];
if (!ts || !sig) throw new Error('Missing Slack headers');
const now = Math.floor(Date.now()/1000);
if (Math.abs(now - parseInt(ts,10)) > 300) throw new Error('Request expired');
let raw = '';
if (typeof $json.body === 'string') raw = $json.body;
else if ($json.body && typeof $json.body === 'object') {
  const p = new URLSearchParams();
  for (const [k,v] of Object.entries($json.body)) { if (v!=null) p.append(k, String(v)); }
  raw = p.toString();
}
if (!raw) throw new Error('No body');
const base = 'v0:' + ts + ':' + raw;
const expected = 'v0=' + crypto.createHmac('sha256', secret).update(base).digest('hex');
const a = Buffer.from(expected), b = Buffer.from(sig);
if (a.length !== b.length || !crypto.timingSafeEqual(a,b)) throw new Error('Invalid signature');
const params = new URLSearchParams(raw);
return [{json:{verified:true, user_id:params.get('user_id'), user_name:params.get('user_name'), channel_id:params.get('channel_id'), text:params.get('text'), response_url:params.get('response_url'), tenant_id:'t1', scope:'slack:user:'+params.get('user_id')}}];

const j = $json || {};
return [{
  json: {
    response_url: j.response_url,
    user_id: j.user_id,
    channel_id: j.channel_id,
    text: j.text,
    tenant_id: j.tenant_id || 't1',
    scope: j.scope || 'slack:user:' + j.user_id
  }
}];

const j = $json || {};

const answer = j.answer ?? j.body?.answer ?? j.data?.answer ?? j.response?.answer ?? null;

const safeAnswer = answer && String(answer).trim().length
  ? String(answer).trim()
  : '⚠️ AI brain is not configured yet.\n\nPlease configure KIMI_API_KEY or OPENAI_API_KEY.\n\n(Infra working; LLM provider missing.)';

if (!j.response_url || String(j.response_url).trim().length < 10) {
  throw new Error('Missing response_url after Merge (should never happen)');
}

return [{
  json: {
    response_url: j.response_url,
    answer: safeAnswer
  }
}];
