const crypto = require('crypto');

const verifyEnabled = ($env.SLACK_SIG_VERIFY_ENABLED || 'true').toLowerCase() !== 'false';
if (!verifyEnabled) {
  const params = new URLSearchParams($json.body);
  return [{json: {
    verified: true,
    bypass: 'ci',
    user_id: params.get('user_id'),
    user_name: params.get('user_name'),
    channel_id: params.get('channel_id'),
    text: params.get('text'),
    response_url: params.get('response_url'),
    tenant_id: 't1',
    scope: 'slack:user:' + params.get('user_id')
  }}];
}

const secret = $env.SLACK_SIGNING_SECRET;
if (!secret) throw new Error('Missing SLACK_SIGNING_SECRET');
const ts = $headers['x-slack-request-timestamp'];
const sig = $headers['x-slack-signature'];
if (!ts || !sig) throw new Error('Missing Slack headers');
const now = Math.floor(Date.now()/1000);
if (Math.abs(now - parseInt(ts,10)) > 300) throw new Error('Request expired');
let raw = '';
if (typeof $json.body === 'string') raw = $json.body;
else if ($json.body && typeof $json.body === 'object') {
  const p = new URLSearchParams();
  for (const [k,v] of Object.entries($json.body)) { if (v!=null) p.append(k, String(v)); }
  raw = p.toString();
}
if (!raw) throw new Error('No body');
const base = 'v0:' + ts + ':' + raw;
const expected = 'v0=' + crypto.createHmac('sha256', secret).update(base).digest('hex');
const a = Buffer.from(expected), b = Buffer.from(sig);
if (a.length !== b.length || !crypto.timingSafeEqual(a,b)) throw new Error('Invalid signature');
const params = new URLSearchParams(raw);
return [{json:{
  verified: true,
  user_id: params.get('user_id'),
  user_name: params.get('user_name'),
  channel_id: params.get('channel_id'),
  text: params.get('text'),
  response_url: params.get('response_url'),
  tenant_id: 't1',
  scope: 'slack:user:' + params.get('user_id')
}}];

const input = $input.first().json;
return [{
  json: {
    slack_response_url: input.response_url,
    slack_user_id: input.user_id,
    slack_text: input.text,
    tenant_id: input.tenant_id,
    scope: input.scope,
    text: input.text
  }
}];

const routerResponse = $json || {};
const preserved = $('Preserve Slack Fields').first().json;

const responseUrl =
  preserved?.slack_response_url ??
  routerResponse.response_url ??
  null;

const answer =
  routerResponse.answer ??
  routerResponse.body?.answer ??
  routerResponse.data?.answer ??
  routerResponse.response?.answer ??
  null;

const safeAnswer =
  answer && String(answer).trim().length
    ? String(answer).trim()
    : "⚠️ AI brain is not configured yet.\n\nPlease configure KIMI_API_KEY or OPENAI_API_KEY in .env and restart services.\n\n(The system infrastructure is working correctly, but no LLM provider is available.)";

return [{
  json: {
    response_url: responseUrl,
    answer: safeAnswer,
    slack_user_id: preserved?.slack_user_id,
    slack_text: preserved?.slack_text
  }
}];
