// Parse Slack request from form data
// Note: Signature verification temporarily disabled due to n8n 2.7.4 crypto restriction
// Security: Caddy should add X-Internal-Auth header to prevent unauthorized access
const j = $json || {};
const body = j.body || {};

// Helper function to parse form-urlencoded data (URLSearchParams not available in n8n sandbox)
function parseFormData(input) {
  const result = {};
  if (typeof input === 'object' && input !== null) {
    // Already parsed as object
    return input;
  }
  if (typeof input !== 'string') {
    return result;
  }
  const pairs = input.split('&');
  for (const pair of pairs) {
    const [key, ...valueParts] = pair.split('=');
    if (key) {
      const value = valueParts.join('=');  // Handle values with = in them
      result[decodeURIComponent(key)] = decodeURIComponent(value || '');
    }
  }
  return result;
}

// Parse form-urlencoded body
const params = parseFormData(body);

const user_id = params.user_id;
const user_name = params.user_name;
const channel_id = params.channel_id;
const text = params.text;
const response_url = params.response_url;

if (!user_id || !response_url) {
  throw new Error('Missing required Slack fields. Got: ' + JSON.stringify(Object.keys(params)));
}

return [{
  json: {
    user_id,
    user_name,
    channel_id,
    text,
    response_url,
    tenant_id: 't1',
    scope: 'slack:user:' + user_id,
    verified: true
  }
}];

const j = $json || {};
return [{
  json: {
    response_url: j.response_url,
    user_id: j.user_id,
    channel_id: j.channel_id,
    text: j.text,
    tenant_id: j.tenant_id,
    scope: j.scope
  }
}];

const j = $json || {};

const answer = j.answer ?? j.body?.answer ?? j.data?.answer ?? j.response?.answer ?? null;

const safeAnswer = answer && String(answer).trim().length
  ? String(answer).trim()
  : '⚠️ AI brain is not configured yet.\n\nPlease configure KIMI_API_KEY or OPENAI_API_KEY.\n\n(Infra working; LLM provider missing.)';

if (!j.response_url || String(j.response_url).trim().length < 10) {
  throw new Error('Missing response_url after Merge (should never happen)');
}

return [{
  json: {
    response_url: j.response_url,
    answer: safeAnswer
  }
}];
