// Validate input
const input = $input.first().json.body;

if (!input.tenant_id || !input.scope || !input.text) {
  return [{
    json: {
      error: 'Missing required fields: tenant_id, scope, text'
    }
  }];
}

// Secret patterns to reject
const secretPatterns = [
  /api[_-]?key/i,
  /bearer\s+[A-Za-z0-9-_.]+/i,
  /-----BEGIN(.*?)PRIVATE KEY-----/i
];

// Check for secrets in text
if (secretPatterns.some(p => p.test(input.text))) {
  return [{
    json: {
      error: 'Rejected: potential secret detected in text'
    }
  }];
}

// Filter facts - only keep those with confidence >= 0.75 OR source == explicit
const filteredFacts = (input.facts || []).filter(f => {
  // Check for secrets in fact fields
  const factText = `${f.subject || ''} ${f.predicate || ''} ${f.object || ''}`;
  if (secretPatterns.some(p => p.test(factText))) {
    return false;
  }

  // Apply commit rules
  if (f.confidence >= 0.75 || input.source === 'explicit') {
    // Reject if subject/predicate/object empty
    if (!f.subject || !f.predicate || !f.object) {
    return false;
    }
    return true;
  }
  return false;
});

return [{
  json: {
    tenant_id: input.tenant_id,
    scope: input.scope,
    text: input.text,
    facts: filteredFacts,
    source: input.source || 'unknown',
    tags: input.tags || []
  }
}];

const data = $input.first().json;

if (data.error) {
  return [{
    json: {
      status: 'error',
      message: data.error
    }
  }];
}

return [{
  json: data
}];

const fs = require('fs');

// Load PCA model
const pca = JSON.parse(fs.readFileSync('/workspace/pca_model.json', 'utf8'));

const response = $input.first().json;
const embedding = response?.embedding?.values;

if (!Array.isArray(embedding) || embedding.length === 0) {
  throw new Error('Invalid embedding response from Gemini');
}

// PCA transformation
const mean = pca.mean;
const components = pca.components;

// Center the embedding
const centered = embedding.map((v, i) => v - mean[i]);

// Project onto principal components
const reduced = components.map(component =>
  component.reduce((sum, weight, i) => sum + weight * centered[i], 0)
);

const original = $('Check Validation').first().json;

return [{
  json: {
    ...original,
    embedding: `[${reduced.join(',')}]`
  }
}];

// Merge Insert Vector result with original data
const insertResult = $input.first().json;
const original = $('Parse Embedding').first().json;

return [{
  json: {
    ...original,
    id: insertResult[0]?.id || null
  }
}];

const result = $input.first().json;
const response = {
  status: 'success',
  message: 'Memory ingested successfully',
  id: result.id || null,
  tenant_id: result.tenant_id,
  scope: result.scope,
  content_hash: result.content_hash || null,
  cached: result.cached || false,
  facts_stored: Array.isArray(result.facts) ? result.facts.length : 0,
  timestamp: new Date().toISOString()
};
return [{ json: response }];
