// Check if we got a cache hit
const result = $input.first().json;
const hasCacheHit = result && result.length > 0 && result[0].id;

if (hasCacheHit) {
  // Return cache hit info
  const original = $('Compute Hash').first().json;
  return [{
    json: {
      ...original,
      cached: true,
      cached_id: result[0].id
    }
  }];
} else {
  // No cache - proceed with embedding generation
  const original = $('Compute Hash').first().json;
  return [{
    json: {
      ...original,
      cached: false
    }
  }];
}

const response = $input.first().json;
const values = response?.embedding?.values;

if (!Array.isArray(values) || values.length === 0) {
  throw new Error('Invalid embedding response from Gemini');
}

// Reduce to 1536 dimensions for pgvector compatibility
const TARGET_DIM = 1536;
const reduced = values.slice(0, TARGET_DIM);
const original = $('If Cached').first().json;

return [{
  json: {
    ...original,
    embedding: `[${reduced.join(',')}]`
  }
}];
