const input = $input.first().json.body;

// Validate required fields
if (!input.actor || !input.action || !input.target || !input.decision) {
  return [{
    json: {
      error: 'Missing required fields: actor, action, target, decision'
    }
  }];
}

// Validate decision value
const validDecisions = ['allowed', 'denied', 'requires_approval'];
if (!validDecisions.includes(input.decision)) {
  return [{
    json: {
      error: 'Invalid decision. Must be: allowed, denied, or requires_approval'
    }
  }];
}

return [{
  json: {
    actor: input.actor,
    action: input.action,
    target: input.target,
    decision: input.decision,
    payload: input.payload || {}
  }
}];

const data = $input.first().json;

if (data.error) {
  return [{
    json: {
      status: 'error',
      message: data.error
    }
  }];
}

return [{
  json: data
}];
