const j = $json || {};
let params = {};

function parseFormData(input) {
  const result = {};
  if (typeof input === 'object' && input !== null) {
    return input;
  }
  if (typeof input !== 'string') {
    return result;
  }
  const pairs = input.split('&');
  for (const pair of pairs) {
    const [key, ...valueParts] = pair.split('=');
    if (key) {
      const value = valueParts.join('=');
      try {
        result[decodeURIComponent(key)] = decodeURIComponent(value || '');
      } catch (e) {
        result[key] = value || '';
      }
    }
  }
  return result;
}

if (j.user_id || j.response_url) {
  Object.assign(params, j);
}
if (j.body && typeof j.body === 'object' && !Array.isArray(j.body)) {
  Object.assign(params, j.body);
}
if (j.body && typeof j.body === 'string') {
  Object.assign(params, parseFormData(j.body));
}
if (j.rawBody && typeof j.rawBody === 'string') {
  Object.assign(params, parseFormData(j.rawBody));
}

const user_id = params.user_id;
const user_name = params.user_name || 'unknown';
const channel_id = params.channel_id;
const text = params.text || '';
const response_url = params.response_url;
const team_id = params.team_id || 'unknown';
const command = params.command || '/ai';

return [{
  json: {
    user_id: user_id || 'unknown',
    user_name,
    channel_id: channel_id || 'unknown',
    text,
    response_url,
    team_id,
    command,
    tenant_id: 't1',
    scope: user_id ? 'slack:user:' + user_id : 'slack:unknown'
  }
}];

const j = $json || {};
return [{
  json: {
    user_id: j.user_id ?? null,
    channel_id: j.channel_id ?? null,
    team_id: j.team_id ?? null,
    command: j.command ?? null,
    text: j.text ?? null,
    response_url: j.response_url ?? null,
    tenant_id: j.tenant_id ?? 't1',
    scope: j.scope ?? 'slack:unknown'
  }
}];

const j = $json || {};

// Router output is in j.body or directly in j
const routerResponse = j.body ?? j;
const answer = routerResponse?.answer ?? routerResponse?.body?.answer ?? null;
const provider = routerResponse?.provider ?? 'unknown';
const status = routerResponse?.status ?? 'unknown';

// NO-BRAIN fallback
const safeAnswer = answer && String(answer).trim().length
  ? String(answer).trim()
  : '⚠️ AI brain is not configured yet.\n\nPlease configure KIMI_API_KEY or OPENAI_API_KEY in .env and restart services.\n\n(Infra is working; only the LLM provider is missing.)';

if (!j.response_url || String(j.response_url).trim().length < 10) {
  throw new Error('Missing response_url after router execution');
}

return [{
  json: {
    response_url: j.response_url,
    answer: safeAnswer,
    provider: provider,
    status: status,
    user_id: j.user_id,
    channel_id: j.channel_id,
    team_id: j.team_id,
    command: j.command,
    text: j.text
  }
}];
